 package org.springframework.data.mongodb.core.query;

import static org.springframework.data.mongodb.core.MongoTemplate.queryForList;

import java.util.List;

import org.springframework.data.mongodb.core.MongoTemplate;
import org.springframework.data.mongodb.core.query.Criteria.Where;
import org.springframework.data.mongodb.core.query.Query;

public class QueryExample {

	public static void main(String[] args) {
		// Create a mongo template
		MongoTemplate mongoTemplate = new MongoTemplate(null, null, null);

		// Create a query
		Query query = new Query();
		query.addCriteria(Where.eq("name", "John"));

		// Execute the query
		List<String> results = queryForList(mongoTemplate, query, String.class);

		// Print the results
		System.out.println(results);
	}
}

class Criteria {

	public static Where eq(String fieldName, Object value) {
		return new Where(fieldName).is(value);
	}
}

class Where {

	private String fieldName;
	private Object value;

	public Where(String fieldName) {
		this.fieldName = fieldName;
	}

	public Where is(Object value) {
		this.value = value;
		return this;
	}
}

